import React, { useRef } from 'react';

function EditableName() {
  const inputRef = useRef(null);

  const handleFocus = () => {
    inputRef.current.textContent = ''; // Clear the text
  };

  return (
    <span
      className="name"
      contentEditable="true"
      id="editableName"
      ref={inputRef}
      onFocus={handleFocus}
    >
      Your name
    </span>
  );
}

export default EditableName;
