import React, { useState } from 'react';
import '../styles/style.css';
import TaskInput from './TaskInput';
import TaskList from './TaskList';

function App() {
  const [tasks, setTasks] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState('');

  const handleAddTask = (newTask) => {
    const taskWithCategory = { ...newTask, category: selectedCategory };
    setTasks([...tasks, taskWithCategory]);
  };

  const handleDelete = (index) => {
    const updatedTasks = tasks.filter((_, i) => i !== index);
    setTasks(updatedTasks);
  };

  const handleEdit = (index, newText) => {
    const updatedTasks = [...tasks];
    updatedTasks[index].text = newText;
    setTasks(updatedTasks);
  };

  const handleCategoryChange = (category) => {
    setSelectedCategory(category);
  };

  return (
    <div className="container">
      <h1>What's up, <span className="name">Your name</span></h1>
      <h4>CREATE A TODO</h4>
      <h5>What's on your todo list?</h5>
      <h5>Pick a category</h5>
      <div className="radio-container">
        <div className={`business ${selectedCategory === 'business' ? 'selected' : ''}`}>
          <input
            type="checkbox"
            className="option1"
            id="businessRadio"
            onClick={() => handleCategoryChange('business')}
          />
          <label htmlFor="businessRadio">Business</label>
        </div>
        <div className={`personal ${selectedCategory === 'personal' ? 'selected' : ''}`}>
          <input
            type="checkbox"
            className="option2"
            id="personalRadio"
            onClick={() => handleCategoryChange('personal')}
          />
          <label htmlFor="personalRadio">Personal</label>
        </div>
      </div>
      <TaskInput onAddTask={handleAddTask} />
      <h4>TODO LIST</h4>
      <TaskList tasks={tasks} onDelete={handleDelete} onEdit={handleEdit} />
    </div>
  );
}

export default App;
