import React, { useState } from 'react';

function CategoryCheckbox({ onCategoryChange }) {
  const [selectedCategory, setSelectedCategory] = useState('');

  const handleCheckboxClick = (category) => {
    setSelectedCategory(category);
    onCategoryChange(category);
  };

  return (
    <div className="radio-container">
      <div className={`business ${selectedCategory === 'business' ? 'selected' : ''}`}>
        <input
          type="checkbox"
          className="option1"
          id="businessRadio"
          onClick={() => handleCheckboxClick('business')}
        />
        <label htmlFor="businessRadio">Business</label>
      </div>
      <div className={`personal ${selectedCategory === 'personal' ? 'selected' : ''}`}>
        <input
          type="checkbox"
          className="option2"
          id="personalRadio"
          onClick={() => handleCheckboxClick('personal')}
        />
        <label htmlFor="personalRadio">Personal</label>
      </div>
    </div>
  );
}

export default CategoryCheckbox;
