import React, { useState } from 'react';
import '../styles/style.css';
import CategoryCheckbox from './CategoryCheckbox';
import TaskInput from './TaskInput';
import TaskList from './TaskList';

function App() {
  const [tasks, setTasks] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState('');

  const handleAddTask = (newTask) => {
    const taskWithCategory = { ...newTask, text: newTask.text, category: selectedCategory };
    setTasks([...tasks, taskWithCategory]);
  };


  const handleDelete = (index) => {
    const updatedTasks = tasks.filter((_, i) => i !== index);
    setTasks(updatedTasks);
  };

  const handleEdit = (index, newText) => {
    const updatedTasks = [...tasks];
    updatedTasks[index].text = newText;
    setTasks(updatedTasks);
  };

  const handleCategoryChange = (category) => {
    setSelectedCategory(category === selectedCategory ? '' : category);
  };

  return (
    <div className="container">
      <h1>What's up, <span className="name">Your name</span></h1>
      <h4>CREATE A TODO</h4>
      <h5>What's on your todo list?</h5>
      <TaskInput onAddTask={handleAddTask} />
      <h5>Pick a category</h5>
      <CategoryCheckbox selectedCategory={selectedCategory} onCategoryChange={handleCategoryChange} />

      <h4>TODO LIST</h4>
      <TaskList tasks={tasks} onDelete={handleDelete} onEdit={handleEdit} />
    </div>
  );
}

export default App;
