import React from 'react';

function TaskList({ tasks, onDelete, onEdit }) {
  const handleComplete = (index) => {
    // Implement the complete task logic
  };

  return (
    <ul className="taskList">
      {tasks.map((task, index) => (
        <li key={index}>
          <input
            type="checkbox"
            className="complete-checkbox"
            onClick={() => handleComplete(index)}
            checked={task.checked}
            readOnly // Make the checkbox read-only to prevent user interaction
          />
          <span className={`taskCategory ${task.category}-border`} />
          <span className="taskText">{task.text}</span> {/* Display the task text */}
          <button
            className="edit-button"
            onClick={() => onEdit(index, task.text)} // Pass the current task text
          >
            Edit
          </button>
          <button className="delete-button" onClick={() => onDelete(index)}>
            Delete
          </button>
        </li>
      ))}
    </ul>
  );
}

export default TaskList;
