import React, { useState } from 'react';

function TaskList({ tasks, onDelete, onEdit, onComplete }) {
  const [editButtonStates, setEditButtonStates] = useState(tasks.map(() => false));
  const [editedTexts, setEditedTexts] = useState(tasks.map(() => ''));

  const handleEditButtonClick = (index) => {
    const newButtonStates = [...editButtonStates];
    const newEditedTexts = [...editedTexts];

    if (newButtonStates[index]) {
      onEdit(index, newEditedTexts[index]);
    }

    newButtonStates[index] = !newButtonStates[index];
    setEditButtonStates(newButtonStates);
  };

  const handleTextChange = (index, newText) => {
    const newEditedTexts = [...editedTexts];
    newEditedTexts[index] = newText;
    setEditedTexts(newEditedTexts);
  };

  return (
    <ul className="taskList">
      {tasks.map((task, index) => (
        <li key={index} className={task.category}>
          <input
            type="checkbox"
            checked={task.completed}
            onChange={() => onComplete(index)}
          />
          <label
            className={`taskText ${task.isEditing ? 'editable' : ''} ${task.completed ? 'completed' : ''}`}
            contentEditable={task.isEditing}
          >
            {task.text}
          </label>
          <button className={`edit-button ${editButtonStates[index] ? 'checked' : ''}`} onClick={() => handleEditButtonClick(index)}>
            {editButtonStates[index] ? '✔' : 'Edit'}
          </button>
          <button className="delete-button" onClick={() => onDelete(index)}>
            Delete
          </button>
        </li>
      ))}
    </ul>
  );
}

export default TaskList;
