import React from 'react';
import './styles/style.css'; // Update the path for the style.css file
import TaskInput from './components/TaskInput'; // Update the path for TaskInput.js
import CategoryCheckbox from './components/CategoryCheckbox'; // Update the path for CategoryCheckbox.js
import TaskList from './components/TaskList';

function App() {
  const [userName, setUserName] = useState('Your name');
  const [tasks, setTasks] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState('');

  const handleAddTask = (taskText) => {
    if (taskText.trim() === '') {
      return;
    }
    const newTask = { text: taskText, category: selectedCategory };
    setTasks([...tasks, newTask]);
  };

  const handleCategoryChange = (category) => {
    setSelectedCategory(category);
  };

  return (
    <div className="container">
      <h1>
        What's up,{' '}
        <span
          className="name"
          contentEditable="true"
          onBlur={(e) => setUserName(e.target.textContent)}
        >
          {userName}
        </span>
      </h1>
      <h4>CREATE A TODO</h4>
      <h5>What's on your todo list?</h5>
      <TaskInput onAddTask={handleAddTask} />
      <h5>Pick a category</h5>
      <CategoryCheckbox
        selectedCategory={selectedCategory}
        onCategoryChange={handleCategoryChange}
      />
      <button className="add">Add todo</button>
      <h4>TODO LIST</h4>
      <TaskList tasks={tasks} />
    </div>
  );
}

export default App;
