import React, { useState } from 'react';

function EditableName() {
  const [name, setName] = useState('');

  const handleFocus = () => {
    if (name === 'Your name') {
      setName('');
    }
  };

  const handleBlur = () => {
    if (name.trim() === '') {
      setName('Your name');
    }
  };

  return (
    <span
      className={`name ${name === 'Your name' ? 'placeholder' : ''}`}
      contentEditable="true"
      onFocus={handleFocus}
      onBlur={handleBlur}
    >
      {name}
    </span>
  );
}

export default EditableName;
